function show(e){document.getElementById(e).classList.contains("hidden")&&document.getElementById(e).classList.remove("hidden")}function hidde(e){document.getElementById(e).classList.add("hidden")}function toggle(e){document.getElementById(e).classList.toggle("hidden")}function toggleItems(e,t){toggle(e),toggle(t)}function noneAfter(e,t){setTimeout(function(){e.classList.add("hidden")},t)}function showMenu(){var e=document.getElementById("menuBurger");e.classList.remove("scale-out-tr"),e.classList.add("scale-in-tr"),e.classList.remove("hidden")}function hideMenu(){var e=document.getElementById("menuBurger");e.classList.remove("scale-in-tr"),e.classList.add("scale-out-tr"),noneAfter(e,300)}